import React, { Component } from 'react'  
import Person from './Person/Person';   
export class App extends Component {  
  state = {  
    date: new Date(),  
  }  

  componentDidMount() {  
    setInterval(  
      () => this.setState({ date: new Date() }),  
      3000  
    );  
  }  
  render() {  
    return (  
      <div>  

          <Person value={this.state.date} /> 

      </div>  
    )  
  }  
}  

export default App
