import React, {PureComponent , useState} from 'react';
import './Terminal.css'
const Terminal = (props) => {
    const [getTerLine,setTerLine] = useState("Iliyan Dimitrov:"+ "■");

    const updateTerminalLine = (e) =>{
        setTerLine("Iliyan Dimitrov:"  + e.target.value.substring(16,e.target.value.length -1) + "■");
        console.log(e.target.value.length);
    }

    return (
        <div  id="main">

            <h1>This is a terminal</h1>
            <p>interactive Portfolio</p>
            <input id="command-line" type="text" spellcheck="false" value={getTerLine} onChange={updateTerminalLine}/>       
        </div>
        
    );
}
export default Terminal;
