import React, { Component } from 'react'
import './App.css';
import Person from './Person/Person';

//This is jsx not HTML

class App extends Component{
  state = {
    persons: [
      {name: 'Bob', age: 28},
      {name: 'Jim', age: 29},
      {name: 'Dom', age: 24}
    ]
  }//Only works with components

  switchNameHandler = () =>{
    let temp = this.state.persons[0];
    this.setState.persons[0] = this.state.persons[1];
    this.setState.persons[1] = temp; 
  }
  render(){
    return(
     <div className = "App">
       <h1>I LOVE YOU</h1>
       <p>This is great!!!!</p>
       <button onClick={this.switchNameHandler}>Switch Name</button>

      <Person name= {this.state.persons[0].name} age={this.state.persons[0].age}>My Hobbies: Racing</Person>
      <Person name= {this.state.persons[1].name} age={this.state.persons[1].age}>My Hobbies: Racing</Person>
      <Person name= {this.state.persons[2].name} age={this.state.persons[2].age}>My Hobbies: Racing</Person>

      </div>
     );
//return React.createElement('div', {className: 'App'}, React.createElement('h1',null,'Howdy!'));
  }
}

export default App;
