import React, { useState } from 'react'
import './App.css';
import Person from './Person/Person';

//This is jsx not HTML

const app = props =>{
 const [personState, setPersonState] =  useState({

      persons: [
        {name: 'Bob', age: 28},
        {name: 'Jim', age: 29},
        {name: 'Dom', age: 24}
      ]
    });  

  const switchNameHandler = () =>{
    let temp1 = personState.persons[0].name
    let temp2 = personState.persons[1].name

    setPersonState({persons: [
      {name: temp2, age: 28},
      {name: temp1, age: 29},
      {name: 'Dom', age: 24}
    ]})
  }

  return(
     <div className = "App">
       <h1>Main Title</h1>
       <p>Different People:</p>
       <button onClick={switchNameHandler}>Switch Name</button>

      <Person name= {personState.persons[0].name} age={personState.persons[0].age}>My Hobbies: Racing</Person>
      <Person name= {personState.persons[1].name} age={personState.persons[1].age}>My Hobbies: Racing</Person>
      <Person name= {personState.persons[2].name} age={personState.persons[2].age}>My Hobbies: Racing</Person>

      </div>
     );


//return React.createElement('div', {className: 'App'}, React.createElement('h1',null,'Howdy!'));
  }
  export default app;


