import React, { Component } from 'react'
import './App.css';
import Person from './Person/Person';

//This is jsx not HTML

class App extends Component{
 state ={
   name: "Bill"
 };

  changeName = (event) =>{
    this.setState({
      name: event.target.value
    });
  }

  render(){
    return(
      <div className = "App">
        <h1>Hello</h1>
        <Person  change = {this.changeName} name = {this.state.name} ></Person>
      </div>
     );
   }

}   
export default App;

