import React, { PureComponent } from 'react';
import './App.css';
import Terminal from'./Terminal/Terminal'

class App extends PureComponent {

  state = {
    tabs:[
      "Terminal"
    ]
  };

  addTab = (tabName) =>{
    switch(tabName){   
      case "Terminal":{
        let tempTabs = [...this.state.tabs];
        tempTabs.push("Terminal X");
        this.setState({tabs:tempTabs})
        break;
      }

      default :{
        break;
      }
    }
  }

  removeTab = (tab) =>{
    const tempTabs = [...this.state.tabs]
    tempTabs.pop(tab);
    this.setState({tabs:tempTabs})

  }

  manipulateTabs = (tabs) =>{
    this.setState({tabs:tabs});
  }
  render () {


    let id = 0;
    const allTabs = (
      <ul id="tabs">
        {this.state.tabs.map((tab) =>{
        return (<li onClick={()=>{this.removeTab(tab)}}className="indTab" key={id++}>{tab}<div>+</div></li>);
        })}
        <li id="addTab"onClick={()=>{this.addTab("Terminal")}}>+</li>
      </ul>
    );


    return (
    <div id="app">
      <ul id="navBar">
        <li>01. About</li>
        <li>02. Experience</li>
        <li>03. Work</li>
        <li>04. Contact Me</li>
      </ul>

      {allTabs}

      <Terminal 
        manipulateTabs = {this.manipulateTabs}
        tabs = {this.state.tabs}>
      </Terminal>

      <p id="footer">Created and Desighned by Iliyan Dimitrov</p>
    </div>
    );
  }
}

export default App;
