import React, { Component } from 'react'  
import Person from './Person/Person';   
export class App extends Component {  
  state = {  
    date: new Date().toLocaleTimeString() 
  }  

  componentDidMount = () => {  
   this.setState({ date: new Date().toLocaleTimeString() }) 

  }  
  render() {  
    return (  
      <div>  

         <div className="Time">
                <p>The Time Is:</p>
                <p> {this.state.date}</p>
            </div>

      </div>  
    )  
  }  
}  

export default App
