import React, { PureComponent , useRef} from 'react';
import './App.css';
import Terminal from'./Terminal/Terminal'

let timeoutID = null;

class App extends PureComponent {



  state = {
    tabs:[],
    buttons:null
  };



  setTimeoutId = (iTimeoutID) =>{
    timeoutID = iTimeoutID;
  }

  addTab = (tabName) =>{
    if(timeoutID !== null)
      clearTimeout(timeoutID);

    let tempTabs = [...this.state.tabs];

    if(tabName !=="Terminal" && tempTabs.indexOf(tabName) !== -1)
      return false;

      console.log("Adding tabName: " + tabName);
    switch(tabName){   
      case "Terminal":{
        tempTabs.push("Terminal");
        break;
      }

      case "About":{
        tempTabs.push("About");
        break;
      }

      case "Experience":{
        tempTabs.push("Experience");
        break;
      }

      case "Work":{
        tempTabs.push("Work");
        break;
      }

      default :{
        return false;
        break;
      }
    }
    this.setState({tabs:tempTabs});
    return true;
  }

  removeTab = (tab) =>{
    if(timeoutID !== null)
      clearTimeout(timeoutID);

    const tempTabs = [...this.state.tabs]
    tempTabs.splice(tempTabs.indexOf(tab), 1);
    this.setState({tabs:tempTabs})

  }

  setButtons = (command) =>{
    this.refs.child.setLine(command);;
    //terminal.current.setLine(command);
  }


  render () {
    const childRef = useRef();

    let id = 0;
    const allTabs = (
      <ul id="tabs">
        <li className="indTab selectedTab">Terminal</li>
        {this.state.tabs.map((tab) =>{
        return (<li className="indTab" key={id++}>{tab}<b onClick={()=>{this.removeTab(tab)}} className="closeX">X</b></li>);
        })}
        <li id="addTab"onClick={()=>{this.addTab("Terminal")}}>+</li>
      </ul>
    );



    return (
    <div id="app">
      <ul id="navBar">
        <li onClick={()=>{this.setState({buttons:"aboutMe"})}}>01. About</li>
        <li>02. Experience</li>
        <li>03. Work</li>
        <li>04. Contact Me</li>
      </ul>

      {allTabs}

      <Terminal 
        ref="child"
        addTab = {this.addTab}
        setTimeoutId = {this.setTimeoutId}
        buttons = {this.state.buttons}
        clearButtons = {this.clearButtons}>

      </Terminal>

      <p id="footer">Created and Desighned by Iliyan Dimitrov</p>
    </div>
    );
  }
}

export default App;
