import React, { useState } from 'react';
import "./Person.css"
const Person = (prop)  => {
   
    const [personState, setPersonState] = useState({
        time: new Date().toLocaleTimeString()
    });
    

    const updateClock = () =>{
        setPersonState({
            time: new Date().toLocaleTimeString()
        });
    }

   
        //Runs this command every second
        setInterval(() => updateClock(), 1000); 
       
        return (
            
            <div className="Complete">
                
                <p>{prop.name}</p>
               
                <p className="Time">{personState.time}</p>

                <input type="text"  value = {prop.name}onChange = {prop.change}/>

               
            </div>
        );
      
}
export default Person;
