import React, { PureComponent, Fragment } from 'react';
import './App.css';
import Terminal from'./Terminal/Terminal'

class App extends PureComponent {

  state = {
    tabs:[
      "Terminal X"
    ]
  };

  addTab = (tabName) =>{
    switch(tabName){   
      case "Terminal":{
        this.setState({tabs:this.state.tabs.push(<Terminal></Terminal>)})
        break;
      }
    }
  }


  render () {
    const id = 0;
    const allTabs = (
      <Fragment>
      {this.state.tabs.map((tab) =>{
      return (<Fragment id={id}>{tab}</Fragment>);
      })}
      </Fragment>
    );

    return (
    <div id="app">
      <ul id="navBar">
        <li>01. About</li>
        <li>02. Experience</li>
        <li>03. Work</li>
        <li>04. Contact Me</li>
      </ul>
      <ul id="tabs">
                <li className="indTab">Terminal  X</li>
                <li id="addTab" oncClick={()=>{this.addTab(Terminal)}}>+</li>
            </ul>
      {allTabs}
      <p id="footer">Created and Desighned by Iliyan Dimitrov</p>
    </div>
    );
  }
}

export default App;
