import React, { Component } from 'react';
class Person extends Component {
   
    state ={
        time: new Date().toLocaleTimeString()
    }
    

    componentDidMount = () => {
        intervalID = setInterval(() => this.updateClock(), 1000);
    }  

    updateClock = () =>{
        this.setState({
            time: new Date().toLocaleTimeString()
        });
    }

    render() {
        return (
            <div className="Time">
                <p>The Time Is:</p>
                <p> {this.state.time}</p>
            </div>
        );
    }   
}
export default Person;
