import React, {PureComponent , useState} from 'react';
import './Terminal.css'
const Terminal = (props) => {
    const [getTerLine,setTerLine] = useState({Value:"Iliyan Dimitrov:■",blink:true});

    const updateTerminalLine = (e) =>{
        //setTerLine("Iliyan Dimitrov:"  + e.target.value.substring(16));
        setTerLine({Value:("Iliyan Dimitrov:"  + e.target.value.substring(16)),blink:getTerLine.blink});

        console.log(e.target.value.length);
    }

    let intervalID = setInterval(() =>{
        if(getTerLine.blink){
            console.log("Blink is true and Value is " + getTerLine.Value);
            setTerLine({Value:(getTerLine.Value + "").replace("■",""),blink:!getTerLine.blink});
        }
        else{
            console.log("Blink is false and Value is " + getTerLine.Value);
            setTerLine({Value: getTerLine.Value.concat("■"),blink:!getTerLine.blink});
        }
    }, 1000);

    return (
        <div  id="main">

            <h1>This is a terminal</h1>
            <p>interactive Portfolio</p>
            <input id="command-line" type="text" spellcheck="false" value={getTerLine.Value} onChange={updateTerminalLine}/>       
        </div>
        
    );
}
export default Terminal;
