import React, { Component } from 'react'  
import Person from './Person/Person'; 
import "./App.css"  

export class App extends Component { 

  state = {
    name: getName
  }

  changeName = (event) =>{
    this.setState({
      name : event.target.value
    });
  }

  render() {  
    return (  
      <div>              
        <Person name={this.state.name} change={this.changeName}/>  
      </div>  
    )  
  }  
}  

export default App
