import React, { PureComponent } from 'react';
import './App.css';
import Terminal from'./Terminal/Terminal'

class App extends PureComponent {

  state = {
    tabs:[
    ]
  };

  addTab = (tabName) =>{
    let tempTabs = [...this.state.tabs];


    if(tempTabs.indexOf(tabName) !== -1)
      return
    switch(tabName){   
      case "Terminal":{
        tempTabs.push("Terminal");
        break;
      }

      default :{
        break;
      }
    }
    this.setState({tabs:tempTabs})
  }

  removeTab = (tab) =>{
    const tempTabs = [...this.state.tabs]
    tempTabs.splice(tempTabs.indexOf(tab), 1);
    this.setState({tabs:tempTabs})

  }

  render () {


    let id = 0;
    const allTabs = (
      <ul id="tabs">
        <li className="indTab">Terminal</li>
        {this.state.tabs.map((tab) =>{
        return (<li className="indTab" key={id++}>{tab}<b onClick={()=>{this.removeTab(tab)}} className="closeX">X</b></li>);
        })}
        <li id="addTab"onClick={()=>{this.addTab("Terminal")}}>+</li>
      </ul>
    );


    return (
    <div id="app">
      <ul id="navBar">
        <li>01. About</li>
        <li>02. Experience</li>
        <li>03. Work</li>
        <li>04. Contact Me</li>
      </ul>

      {allTabs}

      <Terminal 
        addTab = {this.addTab}>
      </Terminal>

      <p id="footer">Created and Desighned by Iliyan Dimitrov</p>
    </div>
    );
  }
}

export default App;
