import React, {useState, Fragment} from 'react';
import './Terminal.css'
const Terminal = (props) => {
    const [getTerLine,setTerLine] = useState({Value:"iliyan@dimitrov:~$ â–®",blink:true});

    const N = () =>{return (<Fragment><br/>â €</Fragment>);}
    const starterArr = [
        (<h2>Iliyan Dimitrov</h2>),
        (<h3>This is a Fully Interactive Portfolio Page with a Simulated Linux Terminal{N()}</h3>),

        (<p>To Explore the Portfolio Either <u>Use the Header Buttons</u></p>),
        (<p>Or <u>Type into the Command Promt</u>{N()}</p>),

        (<p>To Begin Type:</p>),
        (<p>â €â €â €â €â €â €â €[1] or [open aboutMe]: Opens about me</p>),
        (<p>â €â €â €â €â €â €â €[2] or [open experience]: Opens my previous work experience</p>),
        (<p>â €â €â €â €â €â €â €[3] or [open work]: Opens previous projects on GitHub</p>),
        (<p>â €â €â €â €â €â €â €[4] or [run contactMe]: Runs contact me program in terminal</p>),
        (<p>â €â €â €â €â €â €â €[5] or [run snakeGame]: Runs the terminal snake game{N()}</p>)
    ]
    
    const[content,setContent] = useState({
        arr:starterArr
    });

    const updateTerminalLine = (e) =>{
        clearTimeout(timeoutID);
        setTerLine({Value:("iliyan@dimitrov:~$ "  + parseString(e.target.value)),blink:true});
      
    }

    const updateContent =(e) =>{
        e.preventDefault();
        clearTimeout(timeoutID);
        
        setTerLine({Value:"iliyan@dimitrov:~$ ",blink:true});
        
        parseCommand(parseString());
        document.getElementById("command-line").scrollIntoView();
    }

    const parseCommand = (command) =>{
        let commandSelector = command.split(" ")

        let tempArr = [...content.arr];
        tempArr.push(<p>iliyan@dimitrov:~$ {parseString()}</p>);

        switch(commandSelector[0]){
            case "":{
                tempArr.push(<p>iliyan@dimitrov:~$ {parseString()}</p>);
                break;
            }

            case "clear":{
                if(commandSelector.length <= 1){
                    setContent({arr:starterArr});
                    return null;
                }
                else{
                    tempArr.push(<p>iliyan@dimitrov:~$ {parseString()}</p>);
                    tempArr.push(<p>Unknown Argument: {commandSelector[1]}<br/>â €</p>);
                }
                break;
            }

            case "help":{
                if(commandSelector.length <= 1){
                    tempArr.push(<p>â €â €â €â €â €â €â €clear: clears command window</p>);
                    tempArr.push(<p>â €â €â €â €â €â €â €ls: list all files</p>);
                    tempArr.push(<p>â €â €â €â €â €â €â €run [argument]: run a specific program</p>);
                    tempArr.push(<p>â €â €â €â €â €â €â €open [argument]: open a specific document<br/>â €</p>);
                }
                else{ 
                    tempArr.push(<p>Unknown Argument: {commandSelector[1]}<br/>â €</p>);                   
                }
                
                break;
            }

            case "ls":{
                if(commandSelector.length <= 1){
                    tempArr.push(<p>â €â €â €â €â €â €â €File: aboutMe</p>);
                    tempArr.push(<p>â €â €â €â €â €â €â €File: experience</p>);
                    tempArr.push(<p>â €â €â €â €â €â €â €File: work</p>);
                    tempArr.push(<p>â €â €â €â €â €â €â €Program: snakeGame<br/>â €</p>);
                }
                else{ 
                    tempArr.push(<p>Unknown Argument: {commandSelector[1]}<br/>â €</p>);                   
                }
                break;
            }

            case "1":{
                getTerLine.Value = "iliyan@dimitrov:~$ open aboutMe";
                return parseCommand("open aboutMe")
            }

            case "2":{
                getTerLine.Value = "iliyan@dimitrov:~$ open experience";
                return parseCommand("open experience")
             }

             case "3":{
                getTerLine.Value = "iliyan@dimitrov:~$ open work";
                return parseCommand("open work")
             }

             case "4":{
                getTerLine.Value = "iliyan@dimitrov:~$ run contactMe";
                return parseCommand("run contactMe")
             }

             case "5":{
                getTerLine.Value = "iliyan@dimitrov:~$ run snakeGame";
                return parseCommand("run snakeGame")
             }
            case "run":{
                if(commandSelector.length <= 1)
                    tempArr.push(<p>Error Expected Argument run [argument]<br/>â €</p>);

                else{ 
                    tempArr.push(<p>Running Program: {commandSelector[1]} ...<br/>â €</p>);                   
                }
                break;
            }

            case "open":{

                if(commandSelector.length <= 1)
                    tempArr.push(<p>Error Expected Argument open [argument]<br/>â €</p>);

                else{ 
                    switch(commandSelector[1]){
                        case "aboutMe":{
                            if(props.addTab("About"))
                                tempArr.push(<p>Opening File: {commandSelector[1]} ...<br/>â €</p>);
                            else
                                tempArr.push(<p>File {commandSelector[1]} is already open<br/>â €</p>);
                            
                            break;
                        }

                        case "experience":{
                            if(props.addTab("Experience"))
                                tempArr.push(<p>Opening File: {commandSelector[1]} ...<br/>â €</p>);
                            else
                                tempArr.push(<p>File {commandSelector[1]} is already open<br/>â €</p>);
                            
                            break;
                        }

                        case "work":{
                            success = props.addTab("Work")
                            tempArr.push(<p>Opening File: {commandSelector[1]} ...<br/>â €</p>);
                            break;
                        }

                        default:{
                            tempArr.push(<p>Couldn't Find file: {commandSelector[1]}<br/>â €</p>);
                            break;
                        }
                    }

                                       
                }
                break;
            }

            default:
                tempArr.push(<p>{parseString()} is not a recognized command</p>);
                tempArr.push(<p>Type "help" for list of commands<br/>â €</p>);                
                break;
        }

        setContent({arr:tempArr});
    }

    const parseString = (input)=>{
        let bufferIndex = 19;
        if(input !== undefined)
            return input.substring(bufferIndex).replaceAll("â–®","");
        else
