import React from 'react';
import './ContactMe.css';
import '../../Containers/Terminal/Terminal.css'

const ContactMe = () =>{
    const submit = (e) =>{
        e.preventDefault();
        let email = document.getElementById("email").value;
        let subject = document.getElementById("subject").value;
        let content = document.getElementById("content").value;


        var nodemailer = require('nodemailer');

        var transporter = nodemailer.createTransport({
        service: 'gmail',
        auth: {
            user: 'dev.iliyan.dimitrov@gmail.com',
            pass: 'Dcsd243158'
        }
        });

        var mailOptions = {
        from: email,
        to: 'dev.iliyan.dimitrov@gmail.com',
        subject: subject,
        text: content
        };

        transporter.sendMail(mailOptions, function(error, info){
        if (error) {
            alert("Email Failed");
            console.log(error);
        } else {
            alert("Email Sent")
            console.log('Email sent: ' + info.response);
        }
        });
    }
    return(
    <div className = "main">
        <form onSubmit={submit}className="contactForm">
            <h3>Contact Me</h3>
            <p>If you have a question or simply want to say hello.</p>
            <input className="input" id="email" type="email" placeholder="Your Email"></input>
            <input className="input" id="subject" type="text" placeholder="What You Want to Talk About"></input>
            <textarea id="content"></textarea>
            <input type="submit" className="Submit" value="Submit"></input>
        </form>
    </div>
    );
}
export default ContactMe;
